package br.com.entra21.Incremento.Decremento;

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int idade=19;
		
		//aniversario
	

		
	idade = idade+1; //20
	System.out.println(idade);
	
	//ano 29/2 4 em 4 anos
	
	idade= idade+4;
	System.out.println(idade);

	idade++;
	System.out.println(idade);
		
	idade+=2;
	System.out.println(idade);	
		
	idade= idade +2;
	System.out.println(idade);
		
	idade*=2;
	System.out.println(idade);

	idade = idade*2;
	System.out.println(idade);
	
    idade/=2;
    System.out.println(idade);
    
    idade = idade/2;
    System.out.println(idade);
    
    
    idade--;
    System.out.println(idade);
    
    idade-=10;
    System.out.println(idade);
    
    idade=idade-10;
    System.out.println(idade);
  
    
	}

}
